import { connect } from 'react-redux';
import { Route, Switch } from 'react-router-dom';

var addLocation = connect(function (state) {
  return {
    location: state.routing.location
  };
});

var ConnectedRoute = addLocation(Route);

var ConnectedSwitch = addLocation(Switch);

export { addLocation, ConnectedRoute, ConnectedSwitch, ConnectedRoute as Route, ConnectedSwitch as Switch };
